mapper.select:
SELECT PRODUCT_NO, CATEGORY_NAME, PRODUCT_NAME, INPUT_PRICE, SALE_PRICE, SHORTAGE_AMOUNT FROM PRODUCT JOIN CATEGORY USING(CATEGORY_NO) WHERE (PRODUCT_NAME LIKE '%' || #{keyword} || '%' OR TO_CHAR(PRODUCT_NO) LIKE '%' || #{keyword} || '%')
AND CATEGORY_NAME = #{category}
ORDER BY PRODUCT_NO ASC
